import UIKit

enum LNDDebugLevel
{
  enum Level: Int {
    case noChange = 0
    case trace
    case debug
    case info
    case warn
    case error
    case critical
    case individual
    
    var string: String {
      switch self {
      case .noChange:
        return "N/C"
      case .trace:
        return "Trace"
      case .debug:
        return "Debug"
      case .info:
        return "Info"
      case .warn:
        return "Warn"
      case .error:
        return "Error"
      case .critical:
        return "Critical"
      case .individual:
        return "Individual"
      }
    }
    
    static var systemLevelsString: [String] {
      var levelsStrings = [String]()
      var index = 1
      while let levelString = Level(rawValue: index)?.string {
        levelsStrings.append(levelString)
        index += 1
      }
      return levelsStrings
    }
    
    static var subsystemLevelsString: [String] {
      var levelsStrings = [String]()
      var index = 0
      while let level = Level(rawValue: index), level != .individual {
        levelsStrings.append(level.string)
        index += 1
      }
      return levelsStrings
    }
  }
  
  enum FetchSubsystems {
    struct Request { }
    struct Response {
      var result: Result<[String]>
    }
    struct ViewModel {
      var subsystems: [String]
    }
  }
  
  enum ChangeDebugLevel {
    struct Request { }
    struct Reponse {
      var result: Result<Void>
    }
  }
  
  struct ErrorVM {
    var errTitle: String
    var errMsg: String
  }
}
