import UIKit

enum LogConsole
{
  // MARK: Use cases
  enum LogType: String {
    case wallet
    case lnd
  }
  
  enum ReadLog {
    struct Request {
      var logType: LogType
      // var length:
    }
    struct Response {
      var result: Result<String>
    }
    struct ViewModel {
      var displayText: String
    }
  }
  
  enum GetLogURL {
    struct Request {
      var logType: LogType
    }
    struct Response {
      var result: Result<URL>
    }
    struct ViewModel {
      var logURL: URL
    }
  }
  
  enum Error: LocalizedError {
    case logPathNotFound(String)
    
    var localizedDescription: String {
      switch self {
      case .logPathNotFound(let errString):
        return NSLocalizedString("Log path not found for \(errString)", comment: "Log Console Error enum")
      }
    }
  }
  
  struct ErrorVM {
    var errTitle: String
    var errMsg: String
  }
}
