import UIKit

@objc protocol UnlockAppRoutingLogic
{
  func routeToWalletThruRoot()
}

protocol UnlockAppDataPassing
{
  var dataStore: UnlockAppDataStore? { get }
}

class UnlockAppRouter: NSObject, UnlockAppRoutingLogic, UnlockAppDataPassing
{
  weak var viewController: UnlockAppViewController?
  var dataStore: UnlockAppDataStore?
  
  
  // MARK: Routing
  
  func routeToWalletThruRoot() {
    guard let rootViewController = AppDelegate.rootViewController else {
      SLLog.fatal("RootViewController = nil")
    }
    
    // Take a detour. Ask the RootViewController to do the work
    rootViewController.dismiss(animated: false) {
      rootViewController.goWalletNavigation()
    }
  }
  
  
  // MARK: Navigation
  
}
