import UIKit

@objc protocol WalletBalanceRoutingLogic
{
  func routeToWalletMain()
}

protocol WalletBalanceDataPassing
{
  var dataStore: WalletBalanceDataStore? { get }
}

class WalletBalanceRouter: NSObject, WalletBalanceRoutingLogic, WalletBalanceDataPassing
{
  weak var viewController: WalletBalanceViewController?
  var dataStore: WalletBalanceDataStore?
  
  // MARK: Routing
  
  func routeToWalletMain() {
    //    let destinationVC = viewController! as! WalletMainViewController
    //    var destinationDS = destinationVC.router!.dataStore!
    //    passDataToWalletMain(source: dataStore!, destination: &destinationDS)
    navigateToWalletMain(source: viewController!)
  }
  
  
  // MARK: Navigation
  
  func navigateToWalletMain(source: WalletBalanceViewController) {
    guard let navigationController = source.navigationController else {
      SLLog.assert("\(type(of: source)).navigationController = nil")
      return
    }
    navigationController.popViewController(animated: true)
  }
  
  // MARK: Passing data
  
  func passDataToWalletMain(source: WalletBalanceDataStore, destination: inout WalletMainDataStore) { }
}
